// ÊñáÁ´†È°µÈù¢
class Post extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            card: null //ÊâÄÊúâÁöÑ hepta Êï∞ÊçÆ
            , main_card: null //È¶ñÈ°µÂç°Áâá
            , cardList: [] // ÂΩìÂâçÈ°µÈù¢ÁöÑÂç°ÁâáÂàóË°®
            , location: '' // ËÆ∞ÂΩï URL
            , activeNote: 'null'
        };
    }

    componentDidMount() {

        // ËØ∑Ê±Ç hepta Êï∞ÊçÆ getHeptabaseData
        getHeptabaseData.then((res) => {

            let heptabase_blog_data = res.data

            // Â∞ÜÊï∞ÊçÆ‰øùÂ≠òÂà∞ state ‰∏≠
            this.setState({
                card: heptabase_blog_data,
                main_card: res['pages']['about']
            }, () => {

                // Ê∏≤Êüì URL„ÄÅÊï∞ÊçÆ
                this.herfToData()
            })

        })

        // ÁõëÂê¨ notes ÂÆπÂô®ÊªöÂä®
        if (document.getElementsByClassName('notes')[0] !== undefined) {

            // document.getElementsByClassName('notes')[0].addEventListener('scroll', function () {

            //     this.setCardMiniTitleAndStyle()

            // })

            document.getElementsByClassName('notes')[0].addEventListener('scroll', this.setCardMiniTitleAndStyle)
        }

    }

    // Ê†πÊçÆ card id Ëé∑Âèñ card content
    findContent = (id, heptabase_blog_data) => {
        if (heptabase_blog_data === '') {
            return
        }

        let new_card = null
        console.log('Post findContent for:');
        for (let i = 0; i < heptabase_blog_data.cards.length; i++) {

            if (heptabase_blog_data.cards[i]['id'] == id) {

                // Â§ÑÁêÜÂÜÖÂÆπ‰∏≠ÁöÑÂõæÁâá
                heptabase_blog_data.cards[i] = getClearImag(heptabase_blog_data.cards[i])
                console.log('getClearImag done');
                // Â§ÑÁêÜÂÜÖÂÆπ‰∏≠ÁöÑÈìæÊé•
                new_card = getClearCard(heptabase_blog_data.cards[i], heptabase_blog_data.cards)
                heptabase_blog_data.cards[i] = new_card['card']

                return new_card

            }
        }

        return new_card

    }

    // ÊñáÁ´†ÂÜÖÈìæÊé•„ÄÅÂèçÂêëÈìæÊé•ÁÇπÂáª
    handleLinkClick = (link_id, current_id) => {

        // ËÆæÁΩÆÂΩìÂâçÊ¥ªË∑ÉÁöÑÁ¨îËÆ∞ÔºàÁî®Êà∑ÁÑ¶ÁÇπÔºâ
        this.setState({
            activeNote: link_id
        })

        // Â∑≤ÁªèÊòæÁ§∫Ê≠§Âç°ÁâáÂàô‰∏çÊõ¥Êñ∞ URL ÂíåÊï∞ÊçÆ
        let bingo = false
        this.state.cardList.forEach(item => {

            if (link_id == item['card']['id']) {

                bingo = true

            }

        });

        if (bingo) {

            return
        }

        // Âà†Èô§ Link ÊâÄÂú® card ÂêéÁöÑÊâÄÊúâ URL ÂèÇÊï∞
        let url_search = window.location.search
        url_search = url_search.replace('?', '')
        url_search = url_search.replace(/&/gi, '')
        let url_search_list = url_search.split('note-id=')

        let new_url_search = ''
        let current_page_index = -1
        for (let i = 0; i < url_search_list.length; i++) {

            if (url_search_list[i] === '') {
                continue
            }

            if (url_search_list[i] === current_id) {
                // URL ÂèÇÊï∞ === current_id
                current_page_index = i
            } else {
                // URL ÂèÇÊï∞ !== current_id
            }

            if (new_url_search == '') {
                new_url_search += '?note-id=' + url_search_list[i]
            } else {
                new_url_search += '&note-id=' + url_search_list[i]
            }

            //Â¶ÇÊûúÂΩìÂâç id === current_idÔºåÂàôÂøΩÁï•ÂêéÈù¢ÁöÑÊâÄÊúâ ID
            if (current_page_index > -1) {
                break;
            }
        }

        if (new_url_search == '') {
            new_url_search += '?note-id=' + link_id
        } else {
            new_url_search += '&note-id=' + link_id
        }

        // ËÆæÁΩÆ URL
        window.history.pushState({}, '', window.location.origin + '/post' + new_url_search)
        // ËÆ∞ÂΩï URL
        this.setState({
            location: window.location.href
        })


        // Âà†Èô§ URL ‰∏≠‰∏çÂ≠òÂú®ÁöÑ Card
        this.resetCardList()
        // Ê†πÊçÆ URL Ëé∑Âèñ card Êï∞ÊçÆ
        this.herfToData()

    }

    herfToData = () => {

        // È¶ñÈ°µÁöÑÊÉÖÂÜµ
        if (window.location.search === '') {

            // ÊâæÂà∞È¶ñÈ°µÂç°ÁâáÁöÑ ID
            let main_id = this.state.main_card['id']
            // ËÆæÁΩÆ URL
            window.location.replace(window.location.origin + '/post?note-id=' + main_id)

        }

        // ‰ªé URL ‰∏≠Ëé∑Âèñ note idÔºåÊ†πÊçÆ id Ëé∑ÂèñÂç°ÁâáÊï∞ÊçÆ
        let card_list = []
        let url_search = window.location.search
        url_search = url_search.replace('?', '')
        url_search = url_search.replace(/&/gi, '')
        let url_search_list = url_search.split('note-id=')

        for (let i = 0; i < url_search_list.length; i++) {
            if (url_search_list[i] == '') {
                continue
            }
            // Â∞ÜÊï∞ÊçÆ‰øùÂ≠òÂà∞ card list ‰∏≠
            card_list.push(this.findContent(url_search_list[i], this.state.card))

        }

        // Êî∂ÈõÜ card Êï∞ÊçÆÂêé‰∏ÄÊ¨°ÊÄß setStateÔºàÂú® for ÂÜÖÂàÜÂ§öÊ¨° setState ÁöÑËØùÂÆπÊòì‰∏¢Â§±Êï∞ÊçÆÔºâ
        this.setState({
            cardList: card_list,
            activeNote: card_list[card_list.length - 1]['card']['id'] // ËÆæÁΩÆÂΩìÂâçÊ¥ªË∑ÉÁöÑÁ¨îËÆ∞ÔºàÁî®Êà∑ÁÑ¶ÁÇπÔºâ
        })


    }

    // ÂΩì URL ÂèòÂåñÊó∂ÔºàÂ≠êÁªÑ‰ª∂ container ËΩΩÂÖ•ÂÆåÊØïÂêé‰πü‰ºöË∞ÉÁî®Ê≠§ÊñπÊ≥ïÔºâ
    handleHashChange = (url) => {


        setTimeout(() => {
            let note_list = document.getElementsByClassName('container')
            let foucus_note = this.state.activeNote
            console.log(note_list);

            let target_note_offsetLeft = 0
            for (let j = 0; j < note_list.length; j++) {
                let note = note_list[j]

                // ÂÆö‰ΩçÂà∞ÂΩìÂâçÁî®Êà∑ÂÖ≥Ê≥®ÁöÑÁ¨îËÆ∞
                if (note.getAttribute('note_id') === foucus_note) {

                    if (windowWidth > minWidth) {
                        console.log(target_note_offsetLeft);
                        document.getElementsByClassName('notes')[0].scrollTo({ left: j * 650, behavior: 'smooth' })
                    } else {

                    }

                    break;

                }




                target_note_offsetLeft += note.clientWidth
            }

            // ËÆæÁΩÆÊ†∑Âºè„ÄÅÂ∞èÊ†áÈ¢ò
            this.setCardMiniTitleAndStyle()

        }, 100);

        // Â¶ÇÊûú url ÂèëÁîüÂèòÂåñÔºåÂàôÊõ¥Êñ∞Êï∞ÊçÆ
        if (url !== this.state.location) {


            // Â∞ÜÂΩìÂâç URL ‰øùÂ≠òÂà∞ state ‰∏≠
            this.setState({
                location: url
            })

            if (this.state.location !== '') {

                // cardList ‰∏≠‰∏çÂ≠òÂú® URL ÁöÑÊüê‰∏Ä‰∏™ card
                // Âä†ËΩΩÊ≠§ card ÁöÑÊï∞ÊçÆ
                this.herfToData()

            }

        }

        // Âà†Èô§ URL ‰∏≠‰∏çÂ≠òÂú®ÁöÑ Card
        this.resetCardList()

    }

    // Âà†Èô§ URL ‰∏≠‰∏çÂ≠òÂú®ÁöÑ Card
    resetCardList = () => {
        let url = window.location.href
        //ÊØîÂØπ url Âíå cardList
        for (let i = 0; i < this.state.cardList.length; i++) {
            // url ‰∏≠‰∏çÂ≠òÂú®Ê≠§ card
            if (url.indexOf(this.state.cardList[i]['card']['id']) < 0) {


                // Âà†Èô§ card
                this.state.cardList.splice(i, 1)
                this.setState({
                    cardList: this.state.cardList
                })

            } else {
                // url ‰∏≠Â≠òÂú®Ê≠§ card
                // continue
            }

        }

    }

    setCardMiniTitleAndStyle = () => {

        let notes = document.getElementsByClassName('container')

        console.log('setCardMiniTitleAndStyle');

        for (let j = 0; j < notes.length; j++) {

            // Â∞èÊ†áÈ¢ò

            let type = 0 // ËÆ∞ÂΩïÊ†áÈ¢òÂú®Â∑¶‰æßËøòÊòØÂè≥‰æß
            let note // ËÆ∞ÂΩïÈúÄË¶ÅÊ∑ªÂä†Ê†áÈ¢òÁöÑËäÇÁÇπ

            // Âà§Êñ≠Âç°ÁâáÁöÑ‰ΩçÁΩÆÔºåÂΩìÈÅÆÊå°Ââç 1 ‰∏™Âç°ÁâáÊó∂ÔºåÂâç 1 ‰∏™Âç°ÁâáÊòæÁ§∫ÂûÇÁõ¥Ê†áÈ¢ò
            let left_mark = notes[j].getBoundingClientRect().x <= j * 40 + 80
            // Âà§Êñ≠ÊòØÂê¶Ë¶ÅÊòæÁ§∫Âè≥‰æßÊ†áÈ¢ò
            let right_mark = notes[j].getBoundingClientRect().x >= window.innerWidth - (notes.length - j) * 40

            // Â∑¶‰æßÂ∞èÊ†áÈ¢ò
            if (right_mark !== true) {

                if (left_mark) {

                    if (j !== 0) {
                        type = 1
                        note = notes[j - 1]
                    }

                } else {

                    // ÁßªÈô§Â∞èÊ†áÈ¢ò
                    if (j !== 0) {
                        note = notes[j - 1]
                    }

                    if (note !== undefined) {
                        let note_title = note.getElementsByClassName('note_title')[0]
                        if (note_title !== undefined) {

                            // ÁßªÈô§Ê†áÈ¢òÁà∂Á∫ßÂÆπÂô®ÁöÑÁ±ªÂêçÊ†áËÆ∞
                            note.classList.remove('mini')

                            // ÁßªÈô§Ââç‰∏Ä‰∏™ÂÖÉÁ¥†ÁöÑÂûÇÁõ¥Ê†áÈ¢ò
                            note.removeChild(note_title)
                        }
                    }

                }

            }


            // Âè≥‰æßÂ∞èÊ†áÈ¢ò
            if (left_mark !== true) {

                if (right_mark) {

                    type = 2
                    note = notes[j]
                    // Ê∑ªÂä†ÊÇ¨ÊµÆÊ†∑Âºè
                    // note.classList.add('overlay')

                } else {
                    // ÁßªÈô§Â∞èÊ†áÈ¢ò
                    note = notes[j]

                    if (note !== undefined && j !== 0) {
                        let note_title = note.getElementsByClassName('note_title')[0]
                        if (note_title !== undefined) {

                            // ÁßªÈô§Ê†áÈ¢òÁà∂Á∫ßÂÆπÂô®ÁöÑÁ±ªÂêçÊ†áËÆ∞
                            note.classList.remove('mini')

                            // ÁßªÈô§Ââç‰∏Ä‰∏™ÂÖÉÁ¥†ÁöÑÂûÇÁõ¥Ê†áÈ¢ò
                            note.removeChild(note_title)
                        }
                    }

                    // ÁßªÈô§ÊÇ¨ÊµÆÊ†∑Âºè
                    // note.classList.remove('overlay')
                }

            }


            // ÈúÄË¶ÅÊòæÁ§∫Â∞èÊ†áÈ¢ò
            if (type > 0) {

                // Â¶ÇÊûúÂÖÉÁ¥†Êó†Ê†áÈ¢ò
                if (note.classList.contains('mini') == false) {
                    // Ââç‰∏Ä‰∏™ÂÖÉÁ¥†ÊòæÁ§∫ÂûÇÁõ¥Ê†áÈ¢ò
                    let note_title = document.createElement('div')
                    note_title.classList.add('note_title')

                    if (type === 1) {
                        note_title.style.left = (j - 1) * 40 + 'px'
                    } else {
                        note_title.style.right = (notes.length - j) * 40 - 248 + 'px'
                    }


                    // Â∞èÊ†áÈ¢òÊñáÊ°à
                    if (note.getElementsByTagName('H1').length === 0) {
                        // Â¶ÇÊûúÁ¨îËÆ∞‰∏≠Ê≤°Êúâ H1 Ê†áÈ¢ò
                        note_title.innerHTML = note.innerText.substring(0, 6) + '...'
                    } else {
                        note_title.innerHTML = note.getElementsByTagName('H1')[0].innerHTML
