import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useParams, useNavigate } from 'react-router-dom';
import { useLocation } from "react-router";

import Container from '../components/Container'
import Nav from '../components/Nav';
import Footer from '../components/Footer'

import '../index.css'
import 'github-markdown-css'

import { getHeptabaseData, getClearCard, getClearImag } from '../constantFunction'
import { id } from 'date-fns/locale';

import useHash from "../hooks/useHash";

// ÊñáÁ´†È°µÈù¢
class Post extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            card: null //ÊâÄÊúâÁöÑ hepta Êï∞ÊçÆ
            , cardList: [] // ÂΩìÂâçÈ°µÈù¢ÁöÑÂç°ÁâáÂàóË°®
            , location: '' // ËÆ∞ÂΩï URL
        };
    }

    componentDidMount() {

        // ËØ∑Ê±Ç hepta Êï∞ÊçÆ getHeptabaseData
        getHeptabaseData.then((res) => {

            let heptabase_blog_data = res.data

            // Â∞ÜÊï∞ÊçÆ‰øùÂ≠òÂà∞ state ‰∏≠
            this.setState({
                card: heptabase_blog_data
            }, () => {
                // Ê∏≤Êüì URL„ÄÅÊï∞ÊçÆ
                this.herfToData()
            })

        })

    }

    // Ê†πÊçÆ card id Ëé∑Âèñ card content
    findContent = (id, heptabase_blog_data) => {
        if (heptabase_blog_data === '') {
            return
        }

        let new_card = null
        console.log('Post findContent for:');
        for (let i = 0; i < heptabase_blog_data.cards.length; i++) {

            if (heptabase_blog_data.cards[i]['id'] == id) {

                // Â§ÑÁêÜÂÜÖÂÆπ‰∏≠ÁöÑÂõæÁâá
                heptabase_blog_data.cards[i] = getClearImag(heptabase_blog_data.cards[i])
                console.log('getClearImag done');
                // Â§ÑÁêÜÂÜÖÂÆπ‰∏≠ÁöÑÈìæÊé•
                new_card = getClearCard(heptabase_blog_data.cards[i], heptabase_blog_data.cards)
                heptabase_blog_data.cards[i] = new_card['card']

                // ËÆæÁΩÆÁΩëÈ°µÊ†áÈ¢ò
                if (new_card['card']['title'] !== 'About') {
                    document.title = new_card['card']['title']
                } else {
                    document.title = 'Jiang ÁöÑÊï∞Â≠óËä±Âõ≠üå±'
                }

                let cardList = []

                return new_card


            }
        }

        return new_card

    }

    // ÊñáÁ´†ÂÜÖÈìæÊé•„ÄÅÂèçÂêëÈìæÊé•ÁÇπÂáª
    handleLinkClick = (link_id, current_id) => {
        // window.location.search = window.location.search+'&note-id='+id
        // setHash(hash + 'note-id='+id)

        // Â∑≤ÁªèÊòæÁ§∫Ê≠§Âç°ÁâáÂàô‰∏çÊõ¥Êñ∞ URL ÂíåÊï∞ÊçÆ
        let bingo = false
        this.state.cardList.forEach(item => {

            if(link_id==item['card']['id']){
                bingo = true
                // ÂÆö‰ΩçÂà∞Ê≠§Âç°Áâá‰∏ä
                let note_list = document.getElementsByClassName('container')

                for (let j =0;j<note_list.length;j++){
                    let note = note_list[j]
                    if(note.getAttribute('note_id')===link_id){
                        document.getElementsByClassName('notes')[0].scrollTo({ left: note.offsetLeft, behavior: 'smooth' })
                        return
                    }
                }

            }

        });


        if(bingo){

            return
        }


        // Âà†Èô§ Link ÊâÄÂú® card ÂêéÁöÑÊâÄÊúâ URL ÂèÇÊï∞
        let url_search = window.location.search
        url_search = url_search.replace('?', '')
        url_search = url_search.replace(/&/gi, '')
        let url_search_list = url_search.split('note-id=')

        let new_url_search = ''
        let current_page_index = -1
        for (let i = 0; i < url_search_list.length; i++) {

            if (url_search_list[i] === '') {
                continue
            }

            if (url_search_list[i] === current_id) {
                // URL ÂèÇÊï∞ === current_id
                current_page_index = i
            } else {
                // URL ÂèÇÊï∞ !== current_id
            }

            if (new_url_search == '') {
                new_url_search += '?note-id=' + url_search_list[i]
            } else {
                new_url_search += '&note-id=' + url_search_list[i]
            }

            //Â¶ÇÊûúÂΩìÂâç id === current_idÔºåÂàôÂøΩÁï•ÂêéÈù¢ÁöÑÊâÄÊúâ ID
            if (current_page_index > -1) {
                break;
            }
        }

        if (new_url_search == '') {
            new_url_search += '?note-id=' + link_id
        } else {
            new_url_search += '&note-id=' + link_id
        }

        // ËÆæÁΩÆ URL
        window.history.pushState({}, '', window.location.origin + '/post' + new_url_search)
        // ËÆ∞ÂΩï URL
        this.setState({
            location: window.location.href
        })


        // Âà†Èô§ URL ‰∏≠‰∏çÂ≠òÂú®ÁöÑ Card
        this.resetCardList()
        // Ê†πÊçÆ URL Ëé∑Âèñ card Êï∞ÊçÆ
        this.herfToData()


    }

    herfToData = () => {

        // ‰ªé URL ‰∏≠Ëé∑Âèñ note idÔºåÊ†πÊçÆ id Ëé∑ÂèñÂç°ÁâáÊï∞ÊçÆ
        let card_list = []
        let url_search = window.location.search
        url_search = url_search.replace('?', '')
        url_search = url_search.replace(/&/gi, '')
        let url_search_list = url_search.split('note-id=')

        for (let i = 0; i < url_search_list.length; i++) {
            if (url_search_list[i] == '') {
                continue
            }
            // Â∞ÜÊï∞ÊçÆ‰øùÂ≠òÂà∞ card list ‰∏≠
            card_list.push(this.findContent(url_search_list[i], this.state.card))

        }

        // Êî∂ÈõÜ card Êï∞ÊçÆÂêé‰∏ÄÊ¨°ÊÄß setStateÔºàÂú® for ÂÜÖÂàÜÂ§öÊ¨° setState ÁöÑËØùÂÆπÊòì‰∏¢Â§±Êï∞ÊçÆÔºâ
        this.setState({
            cardList: card_list
        })


    }

    // ÂΩì URL ÂèòÂåñÊó∂ÔºàÈÄöÂ∏∏ÊòØÊµèËßàÂô®ÁöÑËøîÂõû„ÄÅÂâçËøõ‰∫ã‰ª∂Ëß¶ÂèëÔºâ
    handleHashChange = (url) => {
        console.log(url);
        // Â¶ÇÊûú url ÂèëÁîüÂèòÂåñÔºåÂàôÊõ¥Êñ∞Êï∞ÊçÆ
        if (url !== this.state.location) {


            // Â∞ÜÂΩìÂâç URL ‰øùÂ≠òÂà∞ state ‰∏≠
            this.setState({
                location: url
            })

            if (this.state.location !== '') {

                // cardList ‰∏≠‰∏çÂ≠òÂú® URL ÁöÑÊüê‰∏Ä‰∏™ card
                // Âä†ËΩΩÊ≠§ card ÁöÑÊï∞ÊçÆ
                this.herfToData()

            }

        }

        // Âà†Èô§ URL ‰∏≠‰∏çÂ≠òÂú®ÁöÑ Card
        this.resetCardList()

    }

    // Âà†Èô§ URL ‰∏≠‰∏çÂ≠òÂú®ÁöÑ Card
    resetCardList = () => {
        let url = window.location.href
        //ÊØîÂØπ url Âíå cardList
        for (let i = 0; i < this.state.cardList.length; i++) {
            // url ‰∏≠‰∏çÂ≠òÂú®Ê≠§ card
            if (url.indexOf(this.state.cardList[i]['card']['id']) < 0) {


                // Âà†Èô§ card
                this.state.cardList.splice(i, 1)
                this.setState({
                    cardList: this.state.cardList
                })

            } else {
                // url ‰∏≠Â≠òÂú®Ê≠§ card
                // continue
            }

        }

    }

    render() {
        if (this.state.card === null) {
            return (<div>loading</div>)
        } else {

            let card_list_dom = []

            for (let i = 0; i < this.state.cardList.length; i++) {
                let card = this.state.cardList[i]

                //ËÆæÁΩÆÁ¨îËÆ∞Ê†∑Âºè
                // left = index*40px; right = index*-40-400
                let note_style = {
                    left: i * 40 + 'px'
                }
                let note = <Container style={note_style} key={card['card']['id']} handleHashChange={this.handleHashChange} handleLinkClick={this.handleLinkClick} card={card} />
                card_list_dom.push(note)
            }

            return (<div>

                <div>
                    <Nav />
                    {/* <Container card={card} /> */}
                    <div className='notes'>
                        {card_list_dom}
                    </div>

                    <Footer />
                </div>

            </div>)
        }
    }



}

export default Post;
