import { useLocation } from "react-router";

import Container from '../components/Container'
import Nav from '../components/Nav';
import Footer from '../components/Footer'

import '../index.css'
import 'github-markdown-css'

import { getHeptabaseData, getClearCard, getClearImag } from '../constantFunction'

// æ–‡ç« é¡µé¢
function Post(props) {
    let { slug } = useParams();
    // è®°å½•å½“å‰æ–‡ç« å¯¹åº”çš„å¡ç‰‡ä¿¡æ¯
    let [card, setCard] = useState(null);
    // è®°å½•æ•°æ®åŠ è½½çŠ¶æ€
    let [isLoading, setLoadingState] = useState(true)

    console.log(slug);
    console.log(window.location);

    window.location = window.location+'&note-id=b81a2f54-ec5b-4de0-9e97-ab841c0e18f9'
    console.log(window.location);

    const useScrollToTop = () => {   //æ³¨æ„è‡ªå®šä¹‰Hooksè¦ç”¨useXXXå®šä¹‰
        const { pathname } = useLocation();
        useEffect(() => {

            console.log('useScrollToTop');

            console.log(window.history);
            window.history.scrollRestoration = 'manual';

            // æ ¹æ®è·³è½¬ç±»åž‹æ¥å†³å®šæ˜¯å¦è¦å®šä½åˆ°é¡¶éƒ¨ï¼Œå¦‚æžœä¸æ˜¯ç‚¹å‡» span æˆ– backLink åˆ™ä¸å›žåˆ°é¡¶éƒ¨

            let nav_type = sessionStorage.getItem('nav_type')

            if (nav_type > -1) {
                // ç‚¹å‡»äº† span æˆ– backLink

                setTimeout(() => {
                    window.scrollTo({
                        top: 0,
                        left: 0,
                        behavior: 'auto'
                    });
                }, 10);

                // window.scrollTo(0, 0);

                // é‡ç½® nav_type
                sessionStorage.setItem('nav_type', -1)
            } else {

                // è¿”å›žä¸Šä¸€é¡µ
                setTimeout(() => {

                    window.scrollTo({
                        top: sessionStorage.getItem('scrollY'),
                        left: 0,
                        behavior: 'auto'
                    });

                    // window.scrollTo(0, sessionStorage.getItem('scrollY'));

                }, 10);

                // window.scrollTo(0, sessionStorage.getItem('scrollY'));


            }


        }, [pathname]);
    }

    useScrollToTop();
    // useReStoreScrollTop();

    useEffect(() => {
        // console.log('scrollTo(0, 0)');
        // window.scrollTo(0, 0);
        console.log('Post useEffect');
        // èŽ·å– URL å‚æ•°

        // 

    })

    // èŽ·å–æ–‡ç« æ•°æ®ã€å¤„ç†æ–‡ç« æ•°æ®
    const setContent = (id) => {
        console.log('setContent');

        // å¦‚æžœå·²ç»èŽ·å–è¿‡æ•°æ®ï¼Œåˆ™ç›´æŽ¥ä»ŽèŽ·å–çš„æ•°æ®ä¸­æå–
        if (card == null) {
            // å­˜å‚¨æ•°æ®çš„å˜é‡
            let heptabase_blog_data

            getHeptabaseData.then((res) => {
                heptabase_blog_data = res.data
                let new_card = null
                console.log('Container setContent for:');
                for (let i = 0; i < heptabase_blog_data.cards.length; i++) {

                    if (heptabase_blog_data.cards[i]['id'] == id) {

                        // å¤„ç†å†…å®¹ä¸­çš„å›¾ç‰‡
                        heptabase_blog_data.cards[i] = getClearImag(heptabase_blog_data.cards[i])
                        console.log('getClearImag done');
                        // å¤„ç†å†…å®¹ä¸­çš„é“¾æŽ¥
                        new_card = getClearCard(heptabase_blog_data.cards[i], heptabase_blog_data.cards)
                        heptabase_blog_data.cards[i] = new_card['card']

                        // è®¾ç½®ç½‘é¡µæ ‡é¢˜
                        if (new_card['card']['title'] !== 'About') {
                            document.title = new_card['card']['title']
                        } else {
                            document.title = 'Jiang çš„æ•°å­—èŠ±å›­ðŸŒ±'
                        }

                        setCard(new_card)
                        setLoadingState(false)

                        break;
                    }
                }

                // 404
                if (new_card == null) {
                    console.log('404');
                    window.location = '/404'
                }
            })
        } else {
            console.log(card);
        }



    }


    // æ ¹æ® URL è·¯å¾„åŠ è½½æ•°æ®



    return <div>

        <div>
            <Nav />
            <Container card={card} />
            <Footer />
        </div>
    </div>;

}

export default Post;
