import React, { useState, useEffect, useRef } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useParams, useNavigate } from 'react-router-dom';
import { useLocation } from "react-router";

import Container from '../components/Container'
import Nav from '../components/Nav';
import Footer from '../components/Footer'
import Loading from '../components/Loading'

import '../index.css'
import 'github-markdown-css'

import { getHeptabaseData, getClearCard, getClearImag } from '../constantFunction'
import { id } from 'date-fns/locale';

import useHash from "../hooks/useHash";



let windowWidth = window.innerWidth
let minWidth = 600

function setCardMiniTitleAndStyle(notes) {
    // Âà§Êñ≠Âç°ÁâáÁöÑ‰ΩçÁΩÆÔºåÂΩìÈÅÆÊå°Ââç 1 ‰∏™Âç°ÁâáÊó∂ÔºåÂâç 1 ‰∏™Âç°ÁâáÊòæÁ§∫ÂûÇÁõ¥Ê†áÈ¢ò
    for (let j = 0; j <= notes.length; j++) {

        if (j === 0) {
            continue
        }

        // Â∞èÊ†áÈ¢ò
        console.log(notes[j].getBoundingClientRect().x);
        console.log(window.innerWidth - (notes.length - j-1)*40);
        console.log('---');
        if (notes[j].getBoundingClientRect().x <= j * 40 + 80 || notes[j-1].getBoundingClientRect().x >= window.innerWidth - (notes.length - j-1)*40) {

            // Â¶ÇÊûúÂâç‰∏ÄÂÖÉÁ¥†Êó†Ê†áÈ¢ò
            if (notes[j - 1].classList.contains('mini') == false) {
                // Ââç‰∏Ä‰∏™ÂÖÉÁ¥†ÊòæÁ§∫ÂûÇÁõ¥Ê†áÈ¢ò
                let note_title = document.createElement('div')
                note_title.classList.add('note_title')

                if(notes[j].getBoundingClientRect().x <= j * 40 + 80){
                    note_title.style.left = (j - 1) * 40 + 'px'
                }else{
                    note_title.style.right = (notes.length-j) * 40 - 248 + 'px'
                }


                // Â∞èÊ†áÈ¢òÊñáÊ°à
                if (notes[j - 1].getElementsByTagName('H1').length === 0) {
                    // Â¶ÇÊûúÁ¨îËÆ∞‰∏≠Ê≤°Êúâ H1 Ê†áÈ¢ò
                    note_title.innerHTML = notes[j - 1].innerText.substring(0, 6) + '...'
                } else {
                    note_title.innerHTML = notes[j - 1].getElementsByTagName('H1')[0].innerHTML
                }


                notes[j - 1].appendChild(note_title)

                notes[j - 1].classList.add('mini')
            }

        } else {

            let note_title = notes[j - 1].getElementsByClassName('note_title')[0]
            if (note_title !== undefined) {

                // ÁßªÈô§Ê†áÈ¢òÁà∂Á∫ßÂÆπÂô®ÁöÑÁ±ªÂêçÊ†áËÆ∞
                notes[j - 1].classList.remove('mini')

                // ÁßªÈô§Ââç‰∏Ä‰∏™ÂÖÉÁ¥†ÁöÑÂûÇÁõ¥Ê†áÈ¢ò
                notes[j - 1].removeChild(note_title)
            }

        }

        // Ê†∑Âºè
        if (notes[j].getBoundingClientRect().x < notes[j - 1].getBoundingClientRect().x + notes[j - 1].getBoundingClientRect().width) {
            notes[j].classList.add('overlay')
        } else {
            notes[j].classList.remove('overlay')
        }

    }
}

// ÊñáÁ´†È°µÈù¢
class Post extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            card: null //ÊâÄÊúâÁöÑ hepta Êï∞ÊçÆ
            , main_card: null //È¶ñÈ°µÂç°Áâá
            , cardList: [] // ÂΩìÂâçÈ°µÈù¢ÁöÑÂç°ÁâáÂàóË°®
            , location: '' // ËÆ∞ÂΩï URL
            , activeNote: 'null'
        };
    }

    componentDidMount() {

        // ËØ∑Ê±Ç hepta Êï∞ÊçÆ getHeptabaseData
