import React, { useState, useEffect, useRef, useUrlState } from 'react';
import { BrowserRouter as Router, Routes, Route, Link, useParams } from 'react-router-dom';
import { useLocation } from "react-router";

import { format } from 'date-fns'

import '../index.css'
import 'github-markdown-css'
import Loading from '../components/Loading'

import ReactMarkdown from 'react-markdown'
import remarkGfm from 'remark-gfm'
import rehypeRaw from 'rehype-raw'

import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter'
import { atomDark } from 'react-syntax-highlighter/dist/esm/styles/prism'
// import docco from 'react-syntax-highlighter/dist/esm/styles/hljs/docco';




import { getHeptabaseData, getClearCard, getClearImag } from '../constantFunction'
import e from 'cors';


// ÊñáÁ´†Ê≠£Êñá
function Container(props) {

    // ËÆ∞ÂΩïÊñáÁ´†ÁöÑ DOM ‰ø°ÊÅØÔºåÁî®Êù•Â§ÑÁêÜ DOM ÂÖÉÁ¥†Ôºå‰æãÂ¶Ç‰øÆÊîπÂõæÁâáÊ†∑Âºè
    let post = useRef(null);

    // ÂΩìÂâçË∑ØÂæÑ‰ø°ÊÅØ
    let path = window.location.pathname

    // Ë∑ØÂæÑ‰∏≠ÂåÖÂê´ÁöÑ post idÔºåÁî®‰ª•Ëé∑ÂèñÊñáÁ´† md ‰ø°ÊÅØ
    let path_id
    if (path.indexOf('/post/') < 0) {

        // Ëã•Ë∑ØÂæÑ‰∏≠‰∏çÂê´ post idÔºåÂàôÂèñÁà∂ÁªÑ‰ª∂ÁöÑ props
        path_id = props.post_id

    } else {
        path_id = path.replace('/post/', '')
    }




    // ËÆ∞ÂΩïÂΩìÂâçÊñáÁ´†ÁöÑ ID
    let [thisPageId, setPageID] = useState('')

    // ËÆ∞ÂΩïËá™ÂÆö‰πâÁöÑ Link Êï∞ÊçÆÔºåÁî®Êù•ÂÆûÁé∞ DOM ÈìæÊé•ÁöÑÈó¥Êé•Ë∑≥ËΩ¨
    let [my_link, setLink] = useState('');



    // Â¶ÇÊûúÂΩìÂâçÈ°µÈù¢ ID ‰∏∫Á©∫ÂàôËé∑ÂèñÊï∞ÊçÆ
    if (thisPageId == '') {
        setPageID(props.post_id)
    }

    // Â¶ÇÊûúÊòØÁßªÂä®Á´ØÂàôÂ¢ûÂä†ÂõæÁâáÁöÑÂ∞∫ÂØ∏
    let isMobile = navigator.userAgent.match(/Mobile/i)
    let mobileSkale = 1
    if (isMobile) {
        mobileSkale = 2
    }

    const handleBackLinkClick = (link_id,current_id) => {
        console.log('handleBackLinkClick');
        console.log(id);
        // ËÆ∞ÂΩïË∑≥ËΩ¨Á±ªÂûã
        sessionStorage.setItem('nav_type', 0)
        // ËÆ∞ÂΩïÂΩìÂâçÊªöÂä®ÁöÑ‰ΩçÁΩÆ
        sessionStorage.setItem('scrollY', window.scrollY)

        props.handleLinkClick(link_id,current_id)

    }

    const { pathname } = useLocation();
    // ÁªÑ‰ª∂ÁîüÂëΩÂë®ÊúüÔºåÁªÑ‰ª∂ËΩΩÂÖ•„ÄÅÊõ¥Êñ∞Êó∂Â∞ÜËß¶ÂèëÊ≠§ÂáΩÊï∞
    useEffect(() => {

        console.log('useEffect');
        props.handleHashChange(window.location.href)

        //ËÆæÁΩÆÈ°µÈù¢ÂÜÖÂÆπ
        // if (card === 'card') {
        //     // Â¶ÇÊûú card Êó†ÂÜÖÂÆπÔºåÂàôËé∑ÂèñÊï∞ÊçÆ

        //     setContent(path_id)

        //     // window.scrollTo(0, 0);
        // } else {


        //     if (card['card']['id'] !== path_id) {

        //         // Â¶ÇÊûú card ÁöÑ ID ‰∏éÂΩìÂâç URL ‰∏≠ÁöÑ ID ‰∏ç‰∏ÄËá¥
        //         console.log('useEffect setContent');
        //         // Ëé∑ÂèñÊñ∞ URL ‰∏≠ÁöÑÊñáÁ´† ID ÂØπÂ∫îÁöÑ md Êï∞ÊçÆ
        //         setContent(path_id)
        //         // Ê∏ÖÁ©∫ÊóßÈ°µÈù¢ÁöÑËá™ÂÆö‰πâÈìæÊé•
        //         setLink('')

        //     }
        // }

        // window.scrollTo(0, 0);

        // dom Âä†ËΩΩÂÆåÊØïÂêé
        if (post.current != null && props['card']['id'] == path_id) {

            // ËÆæÁΩÆÁΩëÊòì‰∫ëÈü≥‰πêÊí≠ÊîæÂô®ÁöÑÂ∞∫ÂØ∏
            console.log(document.getElementById('player'));
            console.log(document.getElementById('music'));
            // console.log(document.getElementById('music').contentWindow.getElementById('infolayer'));

            // ËÆæÁΩÆ img ÁöÑÂ∞∫ÂØ∏
            let article_img = document.getElementsByTagName('img');
            console.log(article_img);

            for (let i = 0; i < article_img.length; i++) {
                let width_key_index = article_img[i]['alt'].indexOf('{{width ')
                if (width_key_index > -1) {
                    let img_width = article_img[i]['alt'].substring(width_key_index, article_img[i]['alt'].length)
                    img_width = img_width.replace('{{width ', '')
                    img_width = img_width.replace('}}', '')

                    article_img[i].setAttribute('style', 'width:' + (Number(img_width.replace('%', '')) * mobileSkale).toString() + '%')
                    article_img[i].style.display = 'block'
                    article_img[i].style.margin = '0 auto'
                }
            }

            // ËÆæÁΩÆ a ÈìæÊé•ÁöÑÁÇπÂáª‰∫ã‰ª∂ÔºåÂ∞Ü a ÊåâÁÖß Link ÁöÑÊñπÂºèËøõË°åË∑≥ËΩ¨ÔºåÈÅøÂÖçÈ°µÈù¢‰∏çÂøÖË¶ÅÁöÑÂà∑Êñ∞
            let article_link = document.getElementsByTagName('span');
            console.log(article_link);
            let links = []

            for (let i = 0; i < article_link.length; i++) {


                if (article_link[i].getAttribute('path') == undefined || article_link[i].getAttribute('path') == null) {
                    // Â¶ÇÊûú DOM ‰∏≠ÁöÑÂÖÉÁ¥†**‰∏ç**ÂåÖÂê´ path Â±ûÊÄßÔºåÂàôË∑≥ËøáÔºàÊúâ path Â±ûÊÄßÁöÑÂÖÉÁ¥†ÊâçÈúÄË¶ÅÂ§ÑÁêÜÔºâ
                    continue
                }

                // ÂàõÂª∫ Link ÂÖÉÁ¥†ÔºåÂΩìÁÇπÂáª‰∏äËø∞ span ÂéüÁîüÊó∂ÔºåÂ∞ÜËß¶Âèë Link ÂÖÉÁ¥†ÁöÑÁÇπÂáª‰∫ã‰ª∂
                let link_temp = <Link className='link_temp' to={article_link[i].getAttribute('path')}>Link</Link>
                links.push(link_temp)


                // DOM ‰∏≠ÁöÑÁâπÂÆöÂÖÉÁ¥†ÁÇπÂáªÊó∂
                article_link[i].onclick = () => {
                    console.log('a click');

                    // Ëé∑ÂèñÂÖÉÁ¥†ÁöÑ path ÂèÇÊï∞ÔºåÊèêÂèñ post id
                    let post_id = article_link[i].getAttribute('path').replace('/post/', '')
                    console.log(post_id);

                    // Ëé∑ÂèñËá™ÂÆö‰πâÁöÑ Link ÂÖÉÁ¥†
                    let my_links = document.getElementsByClassName('link_temp')

                    for (let j = 0; j < my_links.length; j++) {
                        console.log(my_links[j]);
                        console.log(my_links[j].href);

                        // Â¶ÇÊûúËá™ÂÆö‰πâÁöÑ Link ÁöÑ href Â±ûÊÄß‰∏≠ÂåÖÂê´ ÂÖÉÁ¥† path Â±ûÊÄßÁöÑÂÄºÔºåÂàôÂèØÂåπÈÖç
                        if (my_links[j].href.indexOf(article_link[i].getAttribute('path')) >= 0) {

                            // ËÆ∞ÂΩïË∑≥ËΩ¨Á±ªÂûã
                            sessionStorage.setItem('nav_type', 1)
