    // ËÆ∞ÂΩïÊñáÁ´†ÁöÑ DOM ‰ø°ÊÅØÔºåÁî®Êù•Â§ÑÁêÜ DOM ÂÖÉÁ¥†Ôºå‰æãÂ¶Ç‰øÆÊîπÂõæÁâáÊ†∑Âºè
    let post = useRef(null);

    // ÂΩìÂâçË∑ØÂæÑ‰ø°ÊÅØ
    let path = window.location.pathname

    // Ë∑ØÂæÑ‰∏≠ÂåÖÂê´ÁöÑ post idÔºåÁî®‰ª•Ëé∑ÂèñÊñáÁ´† md ‰ø°ÊÅØ
    let path_id
    if (path.indexOf('/post/') < 0) {

        // Ëã•Ë∑ØÂæÑ‰∏≠‰∏çÂê´ post idÔºåÂàôÂèñÁà∂ÁªÑ‰ª∂ÁöÑ props
        path_id = props.post_id

    } else {
        path_id = path.replace('/post/', '')
    }


    // ËÆ∞ÂΩïÊï∞ÊçÆÂä†ËΩΩÁä∂ÊÄÅ
    let [isLoading, setLoadingState] = useState(true)
    console.log(isLoading);
    // ËÆ∞ÂΩïÂΩìÂâçÊñáÁ´†ÁöÑ ID
    let [thisPageId, setPageID] = useState('')

    // ËÆ∞ÂΩïËá™ÂÆö‰πâÁöÑ Link Êï∞ÊçÆÔºåÁî®Êù•ÂÆûÁé∞ DOM ÈìæÊé•ÁöÑÈó¥Êé•Ë∑≥ËΩ¨
    let [my_link, setLink] = useState('');



    // Â¶ÇÊûúÂΩìÂâçÈ°µÈù¢ ID ‰∏∫Á©∫ÂàôËé∑ÂèñÊï∞ÊçÆ
    if (thisPageId == '') {
        setPageID(props.post_id)
    }

    // Â¶ÇÊûúÊòØÁßªÂä®Á´ØÂàôÂ¢ûÂä†ÂõæÁâáÁöÑÂ∞∫ÂØ∏
    let isMobile = navigator.userAgent.match(/Mobile/i)
    let mobileSkale = 1
    if (isMobile) {
        mobileSkale = 2
    }

    const handleBackLinkClick = () => {
        console.log('handleBackLinkClick');

        // ËÆ∞ÂΩïË∑≥ËΩ¨Á±ªÂûã
        sessionStorage.setItem('nav_type', 0)
        // ËÆ∞ÂΩïÂΩìÂâçÊªöÂä®ÁöÑ‰ΩçÁΩÆ
        sessionStorage.setItem('scrollY', window.scrollY)

    }

    const { pathname } = useLocation();
    // ÁªÑ‰ª∂ÁîüÂëΩÂë®ÊúüÔºåÁªÑ‰ª∂ËΩΩÂÖ•„ÄÅÊõ¥Êñ∞Êó∂Â∞ÜËß¶ÂèëÊ≠§ÂáΩÊï∞
    useEffect(() => {

        console.log('useEffect');

        //ËÆæÁΩÆÈ°µÈù¢ÂÜÖÂÆπ
        // if (card === 'card') {
        //     // Â¶ÇÊûú card Êó†ÂÜÖÂÆπÔºåÂàôËé∑ÂèñÊï∞ÊçÆ

        //     setContent(path_id)

        //     // window.scrollTo(0, 0);
        // } else {


        //     if (card['card']['id'] !== path_id) {

        //         // Â¶ÇÊûú card ÁöÑ ID ‰∏éÂΩìÂâç URL ‰∏≠ÁöÑ ID ‰∏ç‰∏ÄËá¥
        //         console.log('useEffect setContent');
        //         // Ëé∑ÂèñÊñ∞ URL ‰∏≠ÁöÑÊñáÁ´† ID ÂØπÂ∫îÁöÑ md Êï∞ÊçÆ
        //         setContent(path_id)
        //         // Ê∏ÖÁ©∫ÊóßÈ°µÈù¢ÁöÑËá™ÂÆö‰πâÈìæÊé•
        //         setLink('')

        //     }
        // }

        // window.scrollTo(0, 0);

        // dom Âä†ËΩΩÂÆåÊØïÂêé
        if (post.current != null && card['card']['id'] == path_id) {

            // ËÆæÁΩÆÁΩëÊòì‰∫ëÈü≥‰πêÊí≠ÊîæÂô®ÁöÑÂ∞∫ÂØ∏
            console.log(document.getElementById('player'));
            console.log(document.getElementById('music'));
            // console.log(document.getElementById('music').contentWindow.getElementById('infolayer'));

            // ËÆæÁΩÆ img ÁöÑÂ∞∫ÂØ∏
            let article_img = document.getElementsByTagName('img');
            console.log(article_img);

            for (let i = 0; i < article_img.length; i++) {
                let width_key_index = article_img[i]['alt'].indexOf('{{width ')
                if (width_key_index > -1) {
                    let img_width = article_img[i]['alt'].substring(width_key_index, article_img[i]['alt'].length)
                    img_width = img_width.replace('{{width ', '')
                    img_width = img_width.replace('}}', '')

                    article_img[i].setAttribute('style', 'width:' + (Number(img_width.replace('%', '')) * mobileSkale).toString() + '%')
                    article_img[i].style.display = 'block'
                    article_img[i].style.margin = '0 auto'
                }
            }

            // ËÆæÁΩÆ a ÈìæÊé•ÁöÑÁÇπÂáª‰∫ã‰ª∂ÔºåÂ∞Ü a ÊåâÁÖß Link ÁöÑÊñπÂºèËøõË°åË∑≥ËΩ¨ÔºåÈÅøÂÖçÈ°µÈù¢‰∏çÂøÖË¶ÅÁöÑÂà∑Êñ∞
            let article_link = document.getElementsByTagName('span');
            console.log(article_link);
            let links = []

