  content = content.replace(old_card, new_card)"}]},{"type":"paragraph","attrs":{"id":"35aff800-8853-49c3-aa06-b98559111da6"},"content":[{"type":"text","text":"需要注意的是  "},{"type":"text","marks":[{"type":"code"}],"text":"react-markdown"},{"type":"text","text":" 在将 "},{"type":"text","marks":[{"type":"code"}],"text":"md"},{"type":"text","text":" 渲染成 "},{"type":"text","marks":[{"type":"code"}],"text":"HTML"},{"type":"text","text":" 时，默认会忽略 "},{"type":"text","marks":[{"type":"code"}],"text":"HTML"},{"type":"text","text":" 标签，通过设置 "},{"type":"text","marks":[{"type":"code"}],"text":"rehypePlugins={[rehypeRaw]}"},{"type":"text","text":" 可以正常解析标签"}]},{"type":"code_block","attrs":{"id":"b0e1bed2-0cdb-40ed-9177-ff8455af11a6","params":"javascript"},"content":[{"type":"text","text":"<ReactMarkdown children={card['card']['content']} rehypePlugins={[rehypeRaw]} } />"}]},{"type":"paragraph","attrs":{"id":"3c707a5f-7577-4142-b18a-05ed6298d452"},"content":[{"type":"text","text":"当 DOM 加载完毕时获取所有前面自定义的 "},{"type":"text","marks":[{"type":"code"}],"text":"span"},{"type":"text","text":" 标签（可以通过 "},{"type":"text","marks":[{"type":"code"}],"text":"className"},{"type":"text","text":" 来获取）然后遍历 "},{"type":"text","marks":[{"type":"code"}],"text":"span"},{"type":"text","text":" 逐个创建 "},{"type":"text","marks":[{"type":"code"}],"text":"Link"},{"type":"text","text":" 元素 "},{"type":"text","marks":[{"type":"code"}],"text":"to"},{"type":"text","text":" 的属性设置为 "},{"type":"text","marks":[{"type":"code"}],"text":"span"},{"type":"text","text":" 标签的 "},{"type":"text","marks":[{"type":"code"}],"text":"path"},{"type":"text","text":" 值，当 "},{"type":"text","marks":[{"type":"code"}],"text":"span"},{"type":"text","text":" 点击时执行 "},{"type":"text","marks":[{"type":"code"}],"text":"Link"},{"type":"text","text":" 标签的点击事件"}]},{"type":"code_block","attrs":{"id":"21a9c454-6191-4ef7-ba8a-6de927942a95","params":"javascript"},"content":[{"type":"text","text":"// 设置 a 链接的点击事件，将 a 按照 Link 的方式进行跳转，避免页面不必要的刷新
  let article_link = document.getElementsByTagName('span');

  let links = []

  for (let i = 0; i < article_link.length; i++) {


      if (article_link[i].getAttribute('path') == undefined || article_link[i].getAttribute('path') == null) {
          // 如果 DOM 中的元素**不**包含 path 属性，则跳过（有 path 属性的元素才需要处理）
          continue
      }

      // 创建 Link 元素，当点击上述 span 原生时，将触发 Link 元素的点击事件
      let link_temp = <Link className='link_temp' to={article_link[i].getAttribute('path')}>Link</Link>
      links.push(link_temp)

      // DOM 中的特定元素点击时
      article_link[i].onclick = () => {

          // 获取元素的 path 参数，提取 post id
          let post_id = article_link[i].getAttribute('path').replace('/post/', '')

          // 获取自定义的 Link 元素
          let my_links = document.getElementsByClassName('link_temp')

          for (let j = 0; j < my_links.length; j++) {

              // 如果自定义的 Link 的 href 属性中包含 元素 path 属性的值，则可匹配
              if (my_links[j].href.indexOf(article_link[i].getAttribute('path')) >= 0) {

                  // 点击
                  my_links[j].click()
                  // 页面滚动到顶部
                  window.scrollTo(0, 0);

                  break
              }
          }

      }
  }

  // 设置自定义 Link 并渲染到 DOM 中
  if (my_link == '' && links.length > 0) {
      setLink(links)
  }

}"}]}]}

